import useStats from "../utils/useStats";
import Stats from "./Stats";
import { useState } from "react";

export default function CountrySelector({ url }) {
  const countries = useStats(url);
  const [selectedCountry, setSelectedCountry] = useState("Canada");

  if (!countries) return <p>Loading..</p>;
  return (
    <div>
      <h2>Current Country --> {selectedCountry}</h2>
      <select
        onChange={e => {
          setSelectedCountry(e.target.value);
        }}
      >
        {Object.entries(countries.countries).map(([country, code]) => (
          <option
            selected={selectedCountry === countries.iso3[code]}
            value={code}
            key={countries.iso3[code]}
          >
            {country}
          </option>
        ))}
      </select>
      <Stats url="https://covid19.mathdro.id/api/countries/canada" />
      <Stats url={`https://covid19.mathdro.id/api/countries/${selectedCountry}`}
      />
    </div>
  );
}
