function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log("Fetching data");
      const data = await fetch("https://covid19.mathdro.id/api").then(data =>
        data.json()
      );
    }
    fetchData();
  }, [input]);
}

function Stats() {
  const stats = useStats();
  return (
    <div>
      <p>Stats: </p>
    </div>
  );
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  );
}
